    <script>
        document.addEventListener("DOMContentLoaded", function() {
            const nhs6innerElements = document.querySelectorAll('.nhs-6-inner .part');
        
            const countIncrease = (element, maxCount, unit = '+') => {
                let count = 0;
                const incrementAmount = getIncrementAmount(maxCount);
                const intervalId = setInterval(() => {
                    count += incrementAmount;
                    element.querySelector('.nhs-h3').textContent = count + unit;
        
                    if (count >= maxCount) {
                        clearInterval(intervalId);
                        element.querySelector('.nhs-h3').textContent = maxCount + unit;
                        element.isCounting = false; // Reset the flag once completed
                    }
                }, 50);
            };
        
            const getIncrementAmount = (n) => {
                if (n < 20) return 1;
                if (n < 40) return 2;
                if (n < 600) return 20;
                return 300;
            };
        
            nhs6innerElements.forEach((element) => {
                const isLPA = element.querySelector('.head').textContent.includes('Salary');
                const maxCount = parseInt(element.querySelector('.nhs-h3').textContent.replace(/[^\d]/g, ''), 10);
        
                element.isCounting = false; // Add a flag to prevent multiple starts
        
                element.addEventListener('mouseenter', () => {
                    if (!element.isCounting) { // Start only if not already counting
                        element.isCounting = true;
                        countIncrease(element, maxCount, isLPA ? ' LPA' : '+');
                    }
                });
        
                element.addEventListener('mouseleave', () => {
                    element.isCounting = false;
                    clearInterval(element.intervalId);
                    element.querySelector('.nhs-h3').textContent = maxCount + (isLPA ? ' LPA' : '+');
                });
            });
        });
    </script>
    <script>
    document.addEventListener("DOMContentLoaded", function () {
    // Delay initialization by 3 seconds
        setTimeout(function () {
        
       // Function to animate numbers
            // Function to animate numbers
            function animateNumber(element, target) {
                    let count = 0;
                    const increment = target / 200; // Adjust the speed of the animation
                
                    const interval = setInterval(() => {
                        count += increment;
                        if (count >= target) {
                            count = target;
                            clearInterval(interval);
                        }
                        element.textContent = Math.ceil(count) + "+"; // Append the '+' symbol
                    }, 10);
                }
                
                // Function to start animation on a specific progress bar
                function startAnimation(progressBar) {
                    const numElement = progressBar.querySelector('.num');
                    
                    // Skip animation for elements with the 'no-animation' class
                    if (numElement.classList.contains('no-animation')) {
                        numElement.textContent = "24*7"; // Set text directly
                    } else {
                        const targetValue = Number(numElement.getAttribute('data-target'));
                
                        // Start animation if not already animated
                        if (!numElement.classList.contains('animated')) {
                            animateNumber(numElement, targetValue);
                            numElement.classList.add('animated'); // Prevent re-triggering
                        }
                    }
                
                    // Add class to show progress bar
                    progressBar.classList.add('show-progress');
                }
                
                // Intersection Observer options with a lower threshold to ensure elements animate on scroll
                const options = {
                    threshold: 0.1 // Trigger when 10% of the element is in view
                };
                
                const observer = new IntersectionObserver((entries, observer) => {
                    entries.forEach(entry => {
                        if (entry.isIntersecting) {
                            startAnimation(entry.target);
                            observer.unobserve(entry.target); // Stop observing once animated
                        }
                    });
                }, options);
                
                // Observing each progress bar to trigger the animation when it enters the viewport
                document.querySelectorAll('.progress-bar').forEach(progressBar => {
                    observer.observe(progressBar);
                
                    // Add hover event to trigger the animation if not already triggered by scroll
                    progressBar.addEventListener('mouseover', () => {
                        startAnimation(progressBar);
                    });
            });                                                                         
        }, 2000); // 2000 ms = 2 seconds
      });
    </script>
