<script>
    document.addEventListener("DOMContentLoaded", () => {
        const videoMap = {
            Mlfirstvideo: "7ulfF4f0ZBM",
            Mlsecondvideo: "kT39ni_zmKQ",
            Mlthirdvideo: "TwJljHVWEbY",
            Mlfourvideo: "GNCITvX6dXg"
        };

        Object.entries(videoMap).forEach(([btnId, videoId]) => {
            const button = document.getElementById(btnId);
            if (button) {
                button.addEventListener("click", () => {
                    const iframe = document.createElement("iframe");
                    iframe.className = "ml-with-iot-preview-vdo";
                    iframe.id = "Previewframevideo";
                    iframe.src = `https://www.youtube.com/embed/${videoId}?autoplay=1&rel=0`;
                    iframe.sandbox = "allow-scripts allow-same-origin allow-presentation";
                    iframe.title = "YouTube video player";
                    iframe.loading = "lazy";
                    iframe.frameBorder = "0";
                    iframe.allow = "accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture";
                    iframe.allowFullscreen = true;

                    const oldIframe = document.getElementById("Previewframevideo");
                    if (oldIframe) oldIframe.replaceWith(iframe);
                });
            }
        });
    });
</script>

